mapper.select:
select gugun_code, gugun_name
from gugun
where sido_code = #{sidoCode};

select a.content_id, a.title, a.addr1, a.zipcode, a.tel, a.first_image, a.latitude, a.longitude, a.sido_code, a.gugun_code
from attraction_info a
where

        and a.sido_code = #{sidoCode}

            and a.gugun_code = #{gugunCode}


            and a.content_type_id = #{contentTypeId}


  ;

select a.content_id, a.title, a.addr1, a.zipcode, a.tel, a.first_image, a.latitude, a.longitude
from attraction_info a
where
 a.content_id in

    #{attraction}

;
