mapper.select:
select id, login_id, last_name, first_name, birth, email, zipcode, main_address, sub_address, mileage, cerified,`role`, created_date
from member
where id = #{id}

mapper.update:
update member
loginPwd = #{loginPwd}, firstName = #{firstName}, lastName = #{lastName}, birth = #{birth}, email = #{email}, zipcode = #{zipcode}, mainAddress = #{mainAddress}, subAddress = #{subAddress},
where id = #{id}
