mapper.update:
update post
set hit = hit + 1
where id = #{id}

mapper.select:
select p.id, p.member_id, m.login_id, p.category, p.title, p.hit, p.created_date
from post p join member m
on p.member_id = m.id

    and ${key} like concat('%', #{word}, '%')


    order by ${orderBy} ${orderByDir}


    order by p.created_date ${orderByDir}

limit #{pagination.limitStart}, #{recordSize}

mapper.update:
update post
set category = #{category},
    title    = #{title},
    content  = #{content}
where id = #{id}
